#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\Internship\new_hire_ai_prototype\Mod3\node_modules\.pnpm\svg-path-bbox@0.0.49\node_modules\svg-path-bbox\src\node_modules;E:\Internship\new_hire_ai_prototype\Mod3\node_modules\.pnpm\svg-path-bbox@0.0.49\node_modules\svg-path-bbox\node_modules;E:\Internship\new_hire_ai_prototype\Mod3\node_modules\.pnpm\svg-path-bbox@0.0.49\node_modules;E:\Internship\new_hire_ai_prototype\Mod3\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/Internship/new_hire_ai_prototype/Mod3/node_modules/.pnpm/svg-path-bbox@0.0.49/node_modules/svg-path-bbox/src/node_modules:/mnt/e/Internship/new_hire_ai_prototype/Mod3/node_modules/.pnpm/svg-path-bbox@0.0.49/node_modules/svg-path-bbox/node_modules:/mnt/e/Internship/new_hire_ai_prototype/Mod3/node_modules/.pnpm/svg-path-bbox@0.0.49/node_modules:/mnt/e/Internship/new_hire_ai_prototype/Mod3/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../svg-path-bbox/src/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../svg-path-bbox/src/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../svg-path-bbox/src/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../svg-path-bbox/src/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
